"use client";

import { useState, useEffect } from 'react';
import { cn } from '@/lib/utils';
import { Newspaper, TrendingUp, Calendar, Image as ImageIcon } from 'lucide-react';

const newsItems = [
  {
    icon: Newspaper,
    title: "Latest Product Updates",
    description: "Weâ€™ve just released several new features to improve your hosting experience, including enhanced scalability and resource management. Learn more about the latest updates.",
    image: "data:image/jpeg;base64," // Placeholder image URL
  },
  {
    icon: TrendingUp,
    title: "Growing Demand for Flexibility",
    description: "As more businesses shift to cloud hosting, weâ€™ve noticed a significant rise in demand for flexible server configurations and billing models. Stay ahead with the latest trends in server management.",
    image: "data:image/jpeg;base64," // Placeholder image URL
  },
  {
    icon: Calendar,
    title: "Upcoming Events & Webinars",
    description: "Join our upcoming webinars to get hands-on experience with our flexible hosting solutions. Weâ€™ll guide you through best practices and share expert insights on optimizing your infrastructure.",
    image: "data:image/jpeg;base64," // Placeholder image URL
  }
];

export function NewsSection() {
  const [activeNews, setActiveNews] = useState(0);
  const [isVisible, setIsVisible] = useState(false);
  const [intervalId, setIntervalId] = useState<NodeJS.Timeout | null>(null);

  useEffect(() => {
    const observer = new IntersectionObserver(
      ([entry]) => {
        if (entry.isIntersecting) {
          setIsVisible(true);
        }
      },
      { threshold: 0.1 }
    );

    const section = document.querySelector('.news-section');
    if (section) observer.observe(section);

    return () => {
      if (section) observer.unobserve(section);
    };
  }, []);

  useEffect(() => {
    if (!isVisible) return;

    const newIntervalId = setInterval(() => {
      setActiveNews((prev) => (prev + 1) % newsItems.length);
    }, 5000);

    setIntervalId(newIntervalId);

    return () => clearInterval(newIntervalId);
  }, [isVisible]);

  // Reset the interval on click
  const handleNewsClick = (idx: number) => {
    setActiveNews(idx);
    if (intervalId) {
      clearInterval(intervalId);
    }
    const newIntervalId = setInterval(() => {
      setActiveNews((prev) => (prev + 1) % newsItems.length);
    }, 7500);
    setIntervalId(newIntervalId);
  };

  const Icon = newsItems[activeNews].icon;

  return (
    <section className="py-20 bg-card/30 news-section">
      <div className="container">
        <div className="max-w-3xl mx-auto text-center mb-16">
          <h2 className="text-3xl md:text-4xl font-bold mb-4">Latest News</h2>
          <p className="text-muted-foreground text-lg">
            Stay updated with the latest news and insights about our flexible hosting solutions. Here are the top 3 recent updates.
          </p>
        </div>

        <div className="grid grid-cols-1 lg:grid-cols-2 gap-12 items-center">
          <div className={cn(
            "relative h-80 rounded-lg overflow-hidden border bg-neutral-800 transition-all duration-300", // Reduced duration for faster transitions
            isVisible ? "opacity-100" : "opacity-0 translate-y-10"
          )}>
            <div className="absolute inset-0  opacity-90" />
            <div className="absolute inset-0 flex items-center justify-center">
              <div className="text-center h-full p-8">
                <img 
                  src={newsItems[activeNews].image} 
                  alt="News Image" 
                  className="absolute top-0 right-0 w-full h-20 object-cover"
                />
                <h3 className="text-2xl font-bold mb-3 mt-20">{newsItems[activeNews].title}</h3>
                <p className="text-lg text-muted-foreground">{newsItems[activeNews].description}</p>
                
              </div>
            </div>
          </div>
          
          <div className="space-y-6">
            {newsItems.map((news, idx) => (
              <div 
                key={idx}
                className={cn(
                  "p-6 border rounded-lg transition-all duration-200 cursor-pointer", // Shortened duration
                  isVisible ? "opacity-100 translate-x-0" : "opacity-0 translate-x-10",
                  activeNews === idx ? "bg-green-900/20 border-green-500" : "bg-card/50 hover:bg-card"
                )}
                style={{ transitionDelay: `${idx * 150}ms` }} // Adjusted transition delay for quicker transitions
                onClick={() => handleNewsClick(idx)} // Reset interval on click
              >
                <div className="flex items-start gap-4">
                  <div className={cn(
                    "h-10 w-10 rounded-full flex items-center justify-center shrink-0 transition-colors",
                    activeNews === idx ? "bg-green-600 text-white" : "bg-muted text-muted-foreground"
                  )}>
                    <news.icon className="h-6 w-6 text-white" /> {/* Icon added here */}
                  </div>
                  <div>
                    <h4 className="text-xl font-bold mb-2">{news.title}</h4>
                    <p className="text-muted-foreground">{news.description}</p>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
    </section>
  );
}